package com.shristi.assignment;

public class FactorialProgram {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, fact=1;
		int num=3;
		for(i=1;i<=num;i++)
		{
			fact=fact*i;
					
		}
		System.out.println("The Factorial of "+num+" is: "+fact);	

	}
}
